package com.lumen.basic;

import java.util.Scanner;

public class EvenNosTill20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even numbers upto 20");
		for (int i = 1; i <= 20; i++) {
			if (i % 2 == 0) {
				System.out.println(i);
			}
		}

	}

}
